var handleSelectAll = function () {
	"use strict";
	$(document).on('click',"[data-click=email-select-all]",function (e) {
		e.preventDefault();
		if ($(this).closest("tr").hasClass("active")) {
			$(".table-email tr").removeClass("active")
		} else {
			$(".table-email tr").addClass("active")
		}
	})
};
var handleSelectSingle = function () {
	"use strict";
	$(document).on('click',"[data-click=email-select-single]",function (e) {
		e.preventDefault();
		var t = $(this).closest("tr");
		if ($(t).hasClass("active")) {
			$(t).removeClass("active")
		} else {
			$(t).addClass("active")
		}
	})
};
var handleEmailRemove = function () {
	"use strict";
	$(document).on('click',"[data-click=email-remove]",function (e) {
		e.preventDefault();
		var t = $(this).closest("tr");
		$(t).fadeOut().remove()
	})
};
var handleEmailHighlight = function () {
	"use strict";
	$(document).on('click',"[data-click=email-highlight]",function (e) {
		e.preventDefault();
		if ($(this).hasClass("text-danger")) {
			$(this).removeClass("text-danger")
		} else {
			$(this).addClass("text-danger")
		}
	})
};
var Inbox = function () {
	"use strict";
	return {
		init: function () {
			handleSelectAll();
			handleSelectSingle();
			handleEmailRemove();
			handleEmailHighlight()
		}
	}
}()
